import org.jointheleague.graphical.robot.Robot;

public class ObedientRobot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Robot rob = new Robot();
rob.penDown();
rob.move(100);
rob.turn(90);
rob.move(100);
rob.turn(90);
rob.move(100);
rob.turn(90);
rob.move(100);
	}

}
